/**
 * Thrown in case a method that can only be called once has been called twice
 * @extends {external:Error}
 * @alias module:errors.HasBeenCalledError
 */
class HasBeenCalledError extends Error {
  /**
   * Create a HasBeenCalledError.
   */
  constructor (methodName) {
    super(`.${methodName}() has been called before`)
    this.name = this.constructor.name
    Error.captureStackTrace(this, this.constructor)
  }
}

module.exports = HasBeenCalledError
