/**
 * Thrown in case a function is called with an argument that doesn't have the expected type or structure.
 * @extends {external:Error}
 * @alias module:errors.IllegalFunctionArgumentError
 */
class IllegalFunctionArgumentError extends Error {
  /**
   * Create an IllegalFunctionArgumentError
   * @param {string} paramName - The name of the incorrect function parameter.
   */
  constructor (paramName) {
    super(`Incorrect function parameter: ${paramName}`)
    this.name = this.constructor.name
    Error.captureStackTrace(this, this.constructor)
  }
}

module.exports = IllegalFunctionArgumentError
