/**
 * Thrown in case there is no session instance but app tries to access it.
 * @extends {external:Error}
 * @alias module:errors.SessionInstanceError
 */
class SessionInstanceError extends Error {
  /**
   * Create a SessionInstanceError.
   */
  constructor () {
    super('Not connected to a db session')
    this.name = this.constructor.name
    Error.captureStackTrace(this, this.constructor)
  }
}

module.exports = SessionInstanceError
