/**
 * Thrown in case a datatype is not supported
 * @extends {external:Error}
 * @alias module:errors.UnsupportedDataType
 */
class UnsupportedDataType extends Error {
  /**
   * Create an UnsupportedDataType
   * @param {string} dataType - The name of the unsupported data tyoe.
   */
  constructor (dataType) {
    super(`Datatype not supported: ${dataType}`)
    this.name = this.constructor.name
    Error.captureStackTrace(this, this.constructor)
  }
}

module.exports = UnsupportedDataType
