const getContextObject = require('../utils/context-object')
const { parseReadUrl } = require('../utils/parse-url')
const handleError = require('../utils/handle-error')
const { getError } = require('../../../errors')

const _convertCustomOperationReturnValue = (returns, result) => {
  if (returns.items) {
    return result
  } else {
    return Array.isArray(result) ? result[0] : result
  }
}

const read = service => {
  return (req, res) => {
    let parsedUrl
    try {
      parsedUrl = parseReadUrl(service.entities, req)
    } catch (err) {
      return handleError(err, service, res)
    }

    const context = getContextObject(service, parsedUrl, req, res)

    if (parsedUrl.customOperation) {
      const operation = parsedUrl.segments[parsedUrl.segments.length - 1]
      const event = parsedUrl.customOperation.startsWith('unbound')
        ? `${service.name}.${operation.name}`
        : operation.name
      return service
        .processEvent(event, context)
        .then(result => {
          // TODO: validation for correct return type
          if (!operation.returns) {
            return res.status(204).send()
          }

          res.status(200)
          res.send(_convertCustomOperationReturnValue(operation.returns, result))
        })
        .catch(err => {
          // Hide errors in generic message but log detailed error
          handleError(err, service, res)
        })
    }

    return service
      .processEvent('READ', context)
      .then(result => {
        if (parsedUrl.isCollection) {
          res.status(200)
          res.send(result)
          return
        }
        if (result.length !== 1) {
          throw getError(404, 'The server has not found a resource matching the Data Services Request URI.')
        }

        res.status(200)
        res.send(result[0])
      })
      .catch(err => {
        // Hide errors in generic message but log detailed error
        handleError(err, service, res)
      })
  }
}

module.exports = read
