const { checkStatic } = require('../../../util/assert')

module.exports = (target, data) => {
  const checkResult = checkStatic(target, data)

  if (checkResult.length !== 0) {
    const err = checkResult[0]
    err.statusCode = 400
    err.message = checkResult.map(e => e.text).join(', ')
    return err
  }
}
