const { statements: { SELECT, INSERT, UPDATE, DELETE } } = require('@sap/cds-ql')

module.exports = service => {
  return {
    SELECT: {
      from: (entity, columns) => {
        return SELECT.from(entity, columns, service.model)
      },
      one: (entity, columns) => {
        return SELECT.one(entity, columns, service.model)
      }
    },
    INSERT: INSERT,
    UPDATE: UPDATE,
    DELETE: DELETE
  }
}
