/**
 * Thrown in case the event handler is not defined.
 * @extends {external:Error}
 * @alias module:errors.EventHandlerNotDefined
 */
class EventHandlerNotDefined extends Error {
  /**
   * Provides fixed error message.
   */
  constructor () {
    super('Event handler must be defined and of type function')
    this.name = this.constructor.name
    Error.captureStackTrace(this, this.constructor)
  }
}

module.exports = EventHandlerNotDefined
