/**
 * Thrown in case a feature is not supported.
 * @extends {external:Error}
 * @alias module:errors.FeatureNotSupported
 */
class FeatureNotSupported extends Error {
  /**
   * Create a FeatureNotSupported.
   */
  constructor (message) {
    super(message || 'This feature is not supported')
    this.name = this.constructor.name
    Error.captureStackTrace(this, this.constructor)
    this.statusCode = 501
  }
}

module.exports = FeatureNotSupported
