/**
 * Thrown in case the CDS service model is not defined.
 * @extends {external:Error}
 * @alias module:errors.NoServiceFound
 */
class NoServiceFound extends Error {
  /**
   * Provides fixed error message.
   */
  constructor () {
    super('No services found, e.g. in package.json or srv/ folder')
    this.name = this.constructor.name
    Error.captureStackTrace(this, this.constructor)
  }
}

module.exports = NoServiceFound
