const { addKeysToData } = require('../utils/handlerUtils')

/**
 * Generic Handler for CREATE requests.
 * Happens before CREATE event is triggered, generates UUIDs for keys.
 *
 * @param context - operation object, that provides error, continuation and other functions as well as information
 * regarding the current operation.
 * @alias module:handlers.beforeCreate
 */
const beforeCreate = () => context => {
  addKeysToData(context.data, context.target)
}

module.exports = beforeCreate
