/**
 * Thrown in case the db client is in an inconsistent state.
 * @extends {external:Error}
 * @alias module:errors.InconsistentClientError
 */
class InconsistentClientError extends Error {
  /**
   * Create a InconsistentClientError.
   */
  constructor () {
    super('Client is in an inconsistent state')
    this.name = this.constructor.name
    Error.captureStackTrace(this, this.constructor)
  }
}

module.exports = InconsistentClientError
