/**
 * Thrown in case a cqn does not have all necessary mandatory properties.
 * @extends {external:Error}
 * @alias module:errors.InvalidCqnObjectError
 */
class InvalidCqnObjectError extends Error {
  /**
   * Create a InvalidCqnObjectError.
   */
  constructor () {
    super('CQN object does not have all mandatory properties')
    this.name = this.constructor.name
    Error.captureStackTrace(this, this.constructor)
  }
}

module.exports = InvalidCqnObjectError
