/**
 * Thrown in case a quoting style is set in CDS, that is not supported.
 * @extends {external:Error}
 * @alias module:errors.InvalidQuotingStyleError
 */
class InvalidQuotingStyleError extends Error {
  /**
   * Create a InvalidQuotingStyleError.
   */
  constructor (quotingStyle) {
    super(`'Quoting style not supported: ${quotingStyle}`)
    this.name = this.constructor.name
    Error.captureStackTrace(this, this.constructor)
  }
}

module.exports = InvalidQuotingStyleError
