const path = require('path');

const AddCommand = require('./add');
const InitCommand = require('./init');

const i18n = require('./i18n/i18n');

const addCommand = new AddCommand();
const initCommand = new InitCommand();

module.exports = {

    init: {
        command: initCommand,
        help: i18n.init_help,
        options: initCommand.cmdOptions,
        flags: initCommand.cmdFlags
    },

    add: {
        command: addCommand,
        help: i18n.addModule_help,
        options: addCommand.cmdOptions,
        flags: addCommand.cmdFlags
    },

    // expose absolute template path for consumption by cap
    templatePath: path.resolve(__dirname, 'templates')

};
