const ARCHETYPE_VERSION = 'RELEASE';
const MODULE_NAME = 'srv';

class MvnArchetypes {

    static odataArchetypeCommand(javaPackage, projectName, odataVersion, verbose = false, debug = false) {
        const commandLine =  [
            `mvn`,
            `archetype:generate`,
            `-DarchetypeGroupId=com.sap.cloud.servicesdk.archetypes`,
            `-DarchetypeArtifactId=${odataVersion}`,
            `-DarchetypeVersion=${ARCHETYPE_VERSION}`,
            `-DinteractiveMode=false`,
            `-Dpackage=${javaPackage}`,
            `-Dversion=1.0-SNAPSHOT`,
            `-DgroupId=sap`,
            `-DartifactId=${projectName}-${MODULE_NAME}`,
            `-Dhdi-container-service=${projectName}-hdi-container`,
            //  `-DtargetFolder=${this.options.moduleName}`, // enable once supported, remove temp/copy logic
            `-Duaa-service-prefix=${projectName}`,
            '-B' // run maven in batch mode (non interactive)
        ];

        if(!verbose) {
            commandLine.push('-q');
        }

        if(debug) {
            commandLine.push('-X');
        }

        return commandLine;
    }

}

module.exports = MvnArchetypes;
